import React, { useEffect, useState } from "react";
import axios from "axios"; // Axios 설치 필요 (npm install axios)

interface StockData {
  id: number;
  name: string;
  price: number;
  stock: string;
}

const MainPage: React.FC = () => {
  const [data, setData] = useState<StockData | null>(null); // 데이터를 저장할 상태
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [error, setError] = useState<string | null>(null); // 에러 상태

  useEffect(() => {
    // 데이터를 가져오는 함수
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/data"); // Flask API 호출
        setData(response.data); // 데이터를 상태에 저장
        setLoading(false); // 로딩 완료
      } catch (err: any) {
        setError(err.message); // 에러 발생 시 상태 저장
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Loading...</div>; // 로딩 중일 때
  if (error) return <div>Error: {error}</div>; // 에러 발생 시

  return (
    <div>
      <h1>Stock Information</h1>
      {data ? (
        <div>
          <p><strong>ID:</strong> {data.id}</p>
          <p><strong>Name:</strong> {data.name}</p>
          <p><strong>Price:</strong> ${data.price}</p>
          <p><strong>Stock Code:</strong> {data.stock}</p>
        </div>
      ) : (
        <p>No data available</p>
      )}
    </div>
  );
};

export default MainPage;
